type ABIs struct {
	registry  abi.ABI
	manager   abi.ABI
	rewards   abi.ABI
	whitelist abi.ABI
	wnat      abi.ABI
	votepower abi.ABI
	submitter abi.ABI
}

func init() {
	abis.registry = mustABI(jsonRegistry)
	abis.manager = mustABI(jsonManager)
	abis.rewards = mustABI(jsonRewards)
	abis.whitelist = mustABI(jsonWhitelist)
	abis.wnat = mustABI(jsonWNAT)
	abis.votepower = mustABI(jsonVotepower)
	abis.submitter = mustABI(jsonSubmitter)
}

func mustABI(json string) abi.ABI {

	abi, err := abi.JSON(strings.NewReader(json))
	if err != nil {
		panic(fmt.Sprintf("invalid ABI JSON: %s", err))
	}

	return abi
}
